REPLACE INTO `{PREFIX}site_htmlsnippets` VALUES ('10','nl_blog','NewsListing Blog Template','0','0','0','<div class=\"nl_summaryPost\">\r\n  <h3><a href=\"[~[+id+]~]\" title=\"[+title+]\">[+title+]</a></h3>\r\n  <div class=\"nl_info\" >By <strong>[+author+]</strong> on [+date+]. <a  href=\"javascript:void(0)\" onclick=\"new Effect.toggle( \'nl_comments_[+id+]\' , \'blind\');\"style=\" cursor : pointer !important;\">Comments\r\n  ([!UserCommentCount?docid=`[+id+]`!])</a></div>\r\n  [+summary+]\r\n  <div id=\"nl_comments_[+id+]\" style=\"display: none;\">\r\n  [!UserComments? &docid=`[+id+]` &canpost=`Registered Users, Site Admins` &makefolder=`0` &postcss=`comment` &titlecss=`commentTitle` &numbercss=`commentNum` &altrowcss=`commentAlt` &authorcss=`commentAuthor` &ownercss=`commentMe` &sortorder=`0`!] \r\n  </div>\r\n  <p class=\"nl_link\">[+link+]</p>\r\n</div>','0');
REPLACE INTO `{PREFIX}site_htmlsnippets` VALUES ('11','footer','Site Template Footer','0','0','0','[(site_name)] is powered by <a href=\"http://modxcms.com/\" title=\"Powered by MODx, Do more with less.\">MODx CMS</a> |\r\n      <span id=\"andreas\">Design by <a href=\"http://andreasviklund.com/\">Andreas Viklund</a></span>\r\n<span id=\"zi\" style=\"display: none\">Designed by <a href=\"http://ziworks.com/\" target=\"_blank\" title=\"E-Business &amp; webdesign solutions\">ziworks</a></span>\r\n\r\n<!-- the modx icon -->\r\n\r\n<div id=\"modxicon\"><h6><a href=\"http://modxcms.com\" title=\"MODx - The XHTML, CSS and Ajax CMS and PHP Application Framework\" id=\"modxicon32\">MODx - The XHTML, CSS and Ajax CMS and PHP Application Framework</a></h6></div>','0');
REPLACE INTO `{PREFIX}site_htmlsnippets` VALUES ('12','meta','Site Template Meta','0','0','0','<p><a href=\"http://validator.w3.org/check/referer\" title=\"This page validates as XHTML 1.0 Transitional\">Valid <abbr title=\"eXtensible HyperText Markup Language\">XHTML</abbr></a></p>                	<p><a href=\"http://jigsaw.w3.org/css-validator/check/referer\" title=\"This page uses valid Cascading Stylesheets\" rel=\"external\">Valid <abbr title=\"W3C Cascading Stylesheets\">css</abbr></a></p>				    <p><a href=\"http://modxcms.com/\" title=\"Powered by MODx, Do more with less.\">MOD<strong>x</strong></a></p>','0');
